INSERT INTO genre (id, name, name_pl, description) VALUES (1, 'Strategy', 'Strategia', 'Gry strategiczne wymagają planowania, decyzji i taktycznego podejścia do osiągnięcia celu.');
INSERT INTO genre (id, name, name_pl, description) VALUES (2, 'Cooperative', 'Kooperacja', 'Gry kooperacyjne to takie, w których gracze współpracują, aby osiągnąć wspólny cel.');
INSERT INTO genre (id, name, name_pl, description) VALUES (3, 'Tile Placement', 'Układanie kafelków', 'Gry z elementem układania kafelków, gdzie gracze tworzą planszę poprzez umieszczanie kafelków obok siebie.');
INSERT INTO genre (id, name, name_pl, description) VALUES (4, 'Deck Building', 'Budowa talii', 'Gry, w których gracze budują swoje talie kart, rozbudowując je w trakcie rozgrywki.');
INSERT INTO genre (id, name, name_pl, description) VALUES (5, 'Adventure', 'Przygoda', 'Gry przygodowe, w których gracze wcielają się w postacie i eksplorują świat, podejmując decyzje.');
INSERT INTO genre (id, name, name_pl, description) VALUES (6, 'Party', 'Towarzyska', 'Gry towarzyskie, idealne na imprezy i spotkania z przyjaciółmi.');
INSERT INTO genre (id, name, name_pl, description) VALUES (7, 'Area Control', 'Kontrola obszaru', 'Gry, w których gracze rywalizują o kontrolę nad obszarami na planszy.');
INSERT INTO genre (id, name, name_pl, description) VALUES (8, 'Worker Placement', 'Umieszczanie pionków', 'Gry, w których gracze umieszczają swoje pionki w określonych miejscach na planszy, aby wykonywać akcje.');
INSERT INTO genre (id, name, name_pl, description) VALUES (9, 'Card Game', 'Gra karciana', 'Gry oparte głównie na kartach, gdzie decyzje graczy opierają się na kombinacjach i strategii kart.');
INSERT INTO genre (id, name, name_pl, description) VALUES (10, 'Exploration', 'Eksploracja', 'Gry oparte na odkrywaniu nowych miejsc, zasobów lub zagadek.');
